declare Person
  @author( Kylin )
  @dateOfCreation( 23-Oct-2017 )

  name : String @key @maxLength( 30 ) @position(0)
  dateOfBirth : Date @key @position(1)
  address : Address
end

declare Customer
  @author( Kylin )
  @dateOfCreation( 23-Oct-2017 )
end

declare GoldenCustomer extends Customer
  @author( Kylin )
  @dateOfCreation( 23-Oct-2017 )

