		this.id = id;
		this.bagTag = bagTag;
		this.location = location;
		this.weight = weight;
		this.timestamp = eventTimestamp;
	}

	public Location getLocation() {
		return location;
	}

	public String getId() {
        return id;
    }

    public BagTag getBagTag() {
		return bagTag;
